DROP TABLE IF EXISTS building_occupancies;
CREATE TABLE building_occupancies (id INTEGER PRIMARY KEY AUTO_INCREMENT NOT NULL, building_id integer, business_unit varchar(255), capacity integer, created_at datetime, updated_at datetime);
DROP TABLE IF EXISTS buildings;
CREATE TABLE buildings (id INTEGER PRIMARY KEY AUTO_INCREMENT NOT NULL, name varchar(255), short_name varchar(255), state varchar(255), suburb varchar(255), building_zone varchar(255) DEFAULT 'CBD', available_for_sip boolean, created_at datetime, updated_at datetime, floors_count integer DEFAULT 0);
DROP TABLE IF EXISTS floors;
CREATE TABLE floors (id INTEGER PRIMARY KEY AUTO_INCREMENT NOT NULL, building_id integer, floor_level varchar(255), active boolean DEFAULT 1, created_at datetime, updated_at datetime, capacity integer DEFAULT 0, opportunites_count integer DEFAULT 0, opportunities_count integer DEFAULT 0);
DROP TABLE IF EXISTS opportunities;
CREATE TABLE opportunities (id INTEGER PRIMARY KEY AUTO_INCREMENT NOT NULL, floor_id integer, num_seats_departing integer, num_seats_arriving integer, business_unit varchar(255), team varchar(255), move_type varchar(255) DEFAULT 'MOVE', notes text, status varchar(255) DEFAULT 'Opportunity', key_move boolean DEFAULT 0, applied boolean DEFAULT 0, created_at datetime, updated_at datetime, est_date date, floor_id_arriving integer);
DROP TABLE IF EXISTS opportunity_items;
CREATE TABLE opportunity_items (id INTEGER PRIMARY KEY AUTO_INCREMENT NOT NULL, opportunity_id integer, from_floor_id integer, from_floor_seats integer, to_floor_id integer, to_floor_seats integer, cost_centre varchar(255), team_name varchar(255), contact_name varchar(255), notes text, created_at datetime, updated_at datetime);
DROP TABLE IF EXISTS schema_migrations;
CREATE TABLE schema_migrations (version varchar(255) NOT NULL);
DROP TABLE IF EXISTS static_items;
CREATE TABLE static_items (id INTEGER PRIMARY KEY AUTO_INCREMENT NOT NULL, list_name varchar(255), list_item varchar(255), created_at datetime, updated_at datetime, list_key varchar(255), list_img varchar(255));
DROP TABLE IF EXISTS users;
CREATE TABLE users (id INTEGER PRIMARY KEY AUTO_INCREMENT NOT NULL, username varchar(255), email varchar(255), crypted_password varchar(255), password_salt varchar(255), persistence_token varchar(255), login_count integer DEFAULT 0 NOT NULL, failed_login_count integer DEFAULT 0 NOT NULL, last_login_at varchar(255), last_login_ip varchar(255), created_at datetime, updated_at datetime, roles_mask integer, time_zone varchar(255));
CREATE INDEX index_floors_on_building_id ON floors (building_id);
CREATE INDEX index_opportunities_on_floor_id ON opportunities (floor_id);
CREATE UNIQUE INDEX unique_schema_migrations ON schema_migrations (version);
